
-- GeekText Project MySQL Database Schema


-- create user's table
CREATE TABLE user (user_id int AUTO_INCREMENT, f_name VARCHAR(255) NOT NULL, 
	l_name VARCHAR(255), nickname VARCHAR(255), email VARCHAR(255) NOT NULL, 
	password VARCHAR(255) NOT NULL, token VARCHAR(32),
	PRIMARY KEY (user_id), UNIQUE (email));


-- create address' table					
CREATE TABLE address (address_id int AUTO_INCREMENT, user_id int NOT NULL, 
	name VARCHAR(100) NOT NULL, street_address VARCHAR(100) NOT NULL,
	state VARCHAR(40) NOT NULL, city VARCHAR(40) NOT NULL, 
	zip_code VARCHAR(10) NOT NULL, country VARCHAR(40) NOT NULL, 
	primary_phone VARCHAR(20) NOT NULL, secondary_phone VARCHAR(20),
	PRIMARY KEY (address_id),
	FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
	

-- create credit card's table
CREATE TABLE credit_card (card_id int AUTO_INCREMENT, user_id int NOT NULL,
	type VARCHAR(20) NOT NULL, number VARCHAR(20) NOT NULL, exp_month int NOT NULL,
	exp_year int NOT NULL, security_code int NOT NULL, nickname VARCHAR(20),
	cardholder VARCHAR(100) NOT NULL, 
	PRIMARY KEY (card_id), 
	FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE);
	
-- create book's table
CREATE TABLE book (book_id int AUTO_INCREMENT, title VARCHAR(60) NOT NULL,
	subtitle VARCHAR(60), authors VARCHAR(60) NOT NULL, publisher VARCHAR(60),
	published_date VARCHAR(20), isbn_13 VARCHAR(20), isbn_10 VARCHAR(20),
	page_count VARCHAR(10), print_type VARCHAR(20), category VARCHAR(20),
	average_rating VARCHAR(20), rating_count VARCHAR(20), language VARCHAR(20),
	image_url VARCHAR(255), description VARCHAR(1500), qty int, price float, sales int,
	PRIMARY KEY (book_id));
	

-- create shopping cart's table
CREATE TABLE cart (cart_id int AUTO_INCREMENT, user_id int NOT NULL,
	book_id int NOT NULL, qty int NOT NULL, saved_for_later tinyint(1),
	price float,
	PRIMARY KEY (cart_id),
	FOREIGN KEY (user_id) REFERENCES user(user_id) ON DELETE CASCADE,
	FOREIGN KEY (book_id) REFERENCES book(book_id) ON DELETE CASCADE);

	

-- *****Note: You MUST modify these procedures with your database credentials (our username: cen_team8) before running these queries.

-- procedure to add an address
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `addAddress`(IN `token` VARCHAR(60), IN `name` VARCHAR(60), 
	IN `street` VARCHAR(60), IN `state` VARCHAR(60), IN `city` VARCHAR(60), IN `zip` VARCHAR(60), IN `country` VARCHAR(60))
	INSERT INTO address (name, street_address, state, city, zip_code, country, primary_phone, user_id) VALUES 
	(name, street, state, city, zip, country, '3055555555', 
	(SELECT user_id 
				FROM user 
				WHERE user.token = token))$$
DELIMITER ;


-- procedure to add book to the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `addToCart`(IN `token` VARCHAR(60), IN `book_id` INT, IN `qty` INT)
	BEGIN
		IF book_id IN (SELECT book_id
					FROM cart
					WHERE cart.book_id = book_id
					AND user_id IN (SELECT user_id
									FROM user
									WHERE user.token = token))
			THEN UPDATE cart SET cart.qty = cart.qty + qty, saved_for_later = 0
						WHERE cart.book_id = book_id 
						AND user_id IN (SELECT user_id 
										FROM user 
										WHERE user.token = token);
		ELSE INSERT INTO cart (book_id, qty, saved_for_later, user_id, price) 
			VALUES (book_id, qty, 0, (SELECT user.user_id 
										FROM user 
										WHERE user.token = token), 
												(SELECT book.price
												FROM book
												WHERE book.book_id = book_id));
		
		END IF;
	END$$
DELIMITER ;


-- to add a credit card to the user's account
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `addCard`(IN `token` VARCHAR(60), IN `holder` VARCHAR(100), 
	IN `type` VARCHAR(20), IN `number` VARCHAR(16), IN `exp` INT(11), IN `year` INT(11), IN `code` INT(11))
	INSERT INTO credit_card (cardholder, type, number, exp_month, exp_year, security_code, user_id) VALUES 
	(holder, type, number, exp, year, code, (SELECT user_id 
				FROM user 
				WHERE user.token = token))$$
DELIMITER ;


-- changes quanty of books in the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `changeQty`(IN `token` VARCHAR(60), IN `id` INT, IN `quantity` INT)
	UPDATE cart SET qty = quantity 
	WHERE book_id = id
	AND user_id IN (SELECT user_id 
					FROM user 
					WHERE user.token = token)$$
DELIMITER ;


-- deletes a book from the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `deleteFromCart`(IN `token` VARCHAR(60), IN `book_id` INT)
	DELETE FROM cart 
	WHERE cart.book_id = book_id
	AND user_id IN (SELECT user_id 
					FROM user 
					WHERE user.token = token)$$
DELIMITER ;


-- removes all books from the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `emptyCart`(IN `token` VARCHAR(60))
	DELETE FROM cart
	WHERE saved_for_later = 0
	AND user_id IN (SELECT user_id 
					FROM user 
					WHERE user.token = token)$$
DELIMITER ;


-- returns all records/books in the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `getCart`(IN `token` VARCHAR(60))
	SELECT title, authors, image_url, cart.qty, cart.price, cart.book_id, saved_for_later
	FROM cart, book 
	WHERE book.book_id = cart.book_id
	AND cart.user_id IN (SELECT user_id 
						FROM user
						WHERE user.token = token)$$
DELIMITER ;


-- returns all books/records in the saved for later list
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `getSavedForLater`(IN `token` VARCHAR(60))
	SELECT title, author, image_url, cart.qty, cart.price, cart.book_id, saved_for_later
	FROM cart, book 
	WHERE book.book_id = cart.book_id
	AND saved_for_later = 1
	AND cart.user_id IN (SELECT user_id 
						FROM user
						WHERE user.token = token)$$
DELIMITER ;


-- to get a subtotal of the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `getCartSubtotal`(IN `token` VARCHAR(60))
	SELECT SUM(qty) as NumItems, SUM(qty * price) as Subtotal
	FROM cart
	WHERE saved_for_later = 0
	AND cart.user_id IN (SELECT user_id 
						FROM user
						WHERE user.token = token)$$
DELIMITER ;


-- get number of books in the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `getCartQty`(IN `token` VARCHAR(60))
	SELECT SUM(qty) as number_of_items
	FROM cart
	WHERE saved_for_later = 0
	AND cart.user_id IN (SELECT user_id 
						FROM user
						WHERE user.token = token)$$
DELIMITER ;


-- moves book from saved fro later list to the shopping cart
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `moveToCart`(IN `token` VARCHAR(60), IN `book_id` INT)
	UPDATE cart 
	SET saved_for_later = 0, qty = 1
	WHERE cart.book_id = book_id
	AND user_id IN (SELECT user_id 
					FROM user 
					WHERE user.token = token)$$
DELIMITER ;


-- moves books from the shopping cart to the saved for later list
DELIMITER $$
	CREATE DEFINER=`cen_team8`@`localhost` PROCEDURE `saveForLater`(IN `token` VARCHAR(60), IN `book_id` INT)
	UPDATE cart 
	SET saved_for_later = 1, qty = 0
	WHERE cart.book_id = book_id
	AND user_id IN (SELECT user_id 
					FROM user 
					WHERE user.token = token)$$
DELIMITER ;
	
	
	
--
-- Dumping data for table `book`
--

INSERT INTO `book` (`book_id`, `title`, `subtitle`, `authors`, `authorbio`, `publisher`, `published_date`, `isbn_13`, `isbn_10`, `page_count`, `print_type`, `category`, `average_rating`, `rating_count`, `language`, `image_url`, `description`, `qty`, `price`, `sales`) VALUES
(19, 'The Long Haul', NULL, 'Jeff Kinney', 'In 1998 Jeff came up with the idea for Diary of a Wimpy Kid, a story about a middle-school weakling named Greg Heffley. Jeff worked on his book for almost eight years before showing it to a publisher in New York. Jeff Kinney was born in 1971 in Maryland and attended the University of Maryland in the early 1990s. It was there that Jeff ran a comic strip called “Igdoof” in the campus newspaper, and he knew he wanted to be a cartoonist. However, Jeff was not successful in getting his comic strip syndicated after college, and in 1998 he started writing down ideas for Diary of a Wimpy Kid, which he hoped to turn into a book. Jeff worked on the book for six years before publishing it online on Funbrain.com in daily installments. To date, the online version of Diary of a Wimpy Kid has more than 80 million visits, and is typically read by more than 70,000 kids a day. In 2006, Jeff signed a multi-book deal with publisher Harry N. Abrams, Inc. to turn Diary of a Wimpy Kid into a print series. The first Diary of a Wimpy Kid book was published in 2007 and became an instant bestseller. Just a year later, more than 100,000 copies were in print in the United States alone. With each subsequent book, in-print numbers continue to grow exponentially both in the U.S. and abroad. There are now more than 200 million copies of the series in print worldwide. The Diary of a Wimpy Kid series has been a fixture on the USA Today, Wall Street Journal, and Publishers Weekly bestseller lists. The series has remained on the New York Times bestseller lists since the publication of the first book, for more than 500 weeks total, and more than 350 on the series list. The books are currently available in 73 editions in 61 languages. Since initial publication in 2007, the series has gone on to win many regional and national awards around the globe including two Children’s Choice Book Awards and six Nickelodeon Kids’ Choice Awards for Favorite Book. Jeff Kinney was named one of Time magazine’s most influential people in the world. Jeff Kinney is also the creator of Poptropica, which was named one of Time’s 50 Best Websites. He spent his childhood in the Washington, D.C., area and moved to New England in 1995. Jeff lives with his wife and two sons in Massachusetts, where they own a bookstore, An Unlikely Story.  https://wimpykid.com/about-the-author/', 'Puffin', '2017-05-15', '9780143785897', '9780143785897', '224', 'BOOK', 'Automobile travel', '2', '26', 'en', 'http://books.google.com/books/content?id=Bu6XtAEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'A family road trip is supposed to be a lot of fun . . . unless, of course, you\'re the Heffleys. The journey starts off full of promise, then quickly takes several wrong turns. Gas station bathrooms, crazed seagulls, a fender bender, and a runaway pig-not exactly Greg Heffley\'s idea of a good time. But even the worst road trip can turn into an adventure-and this is one the Heffleys won\'t soon forget. Readers love The Diary of a Wimpy KidSeries. The USA Today, Publishers Weekly, Wall Street Journal, and # 1New York Timesbestsellers- \'In the publishing world, Kinney is a rock star.\' NPR\'s \'Backseat Book Club\'', 10, 21.19, 4),
(20, 'Ghosts', NULL, 'Raina Telgemeier', 'Raina Telgemeier is the author and illustrator of the graphic novels Smile, Drama, Sisters, and Ghosts, all #1 New York Times bestsellers. She also adapted and illustrated four graphic novel versions of Ann M. Martin’s Baby-sitters Club series, and has contributed short stories to many anthologies. Raina’s accolades include three Eisner Awards, a Boston Globe-Horn Book Honor, a Stonewall Honor, and many Best-of and Notables lists. Raina lives and works in San Francisco, CA.  https://goraina.com/about', 'Scholastic Inc.', '2016-09-13', '0545540674', '0545540674', '256', 'BOOK', 'Juvenile Fiction', '4', '69', 'en', 'http://books.google.com/books/content?id=Qg5YCwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'From Raina Telgemeier, the #1 New York Times bestselling, multiple Eisner Award-winning author of Smile, Drama, and Sisters! Catrina and her family are moving to the coast of Northern California because her little sister, Maya, is sick. Cat isn\'t happy about leaving her friends for Bahía de la Luna, but Maya has cystic fibrosis and will benefit from the cool, salty air that blows in from the sea. As the girls explore their new home, a neighbor lets them in on a secret: There are ghosts in Bahía de la Luna. Maya is determined to meet one, but Cat wants nothing to do with them. As the time of year when ghosts reunite with their loved ones approaches, Cat must figure out how to put aside her fears for her sister\'s sake - and her own. Raina Telgemeier has masterfully created a moving and insightful story about the power of family and friendship, and how it gives us the courage to do what we never thought possible.', 10, 25.65, 5),
(18, 'How to Babysit a Grandma', NULL, 'Jean Reagan', 'Welcome to my website! I am a children\'s author living in Salt Lake City, Utah. I was born in Fairhope, Alabama, but spent most of my childhood in Japan. My book, HOW TO BABYSIT A GRANDPA, (2012, Knopf Books for Young Readers) is a humorous picture book about a little boy and his grandpa. It’s a NYTimes bestseller and an Indie Bestseller. The companion book, HOW TO BABYSIT A GRANDMA, (2014) is a sleepover story about a grandmother and her granddaughter. It\'s also a NYTimes and Indie bestseller. These two books have become the start of a HOW-TO \"series.\" HOW TO SURPRISE A DAD came out in March, 2015 and HOW TO CATCH SANTA came out in October, 2015. My previous book, ALWAYS MY BROTHER, (2009, Tilbury House Publishers) is a story about a serious topic -- sibling loss. In 2017, HOW TO RAISE A MOM and HOW TO GET YOUR TEACHER READY were published. A \"spooky\" Halloween book, HOW TO SCARE A GHOST, joined the series in 2018. I have one more book under contract, so stay tuned! In the summer, my husband and I serve as backcountry volunteers in Grand Teton National Park. We live in a patrol cabin that has no running water or electricity. Elk, deer, bears, and an occasional mountain lion are our neighbors.  https://www.jeanreagan.com', 'Knopf Books for Young Readers', '2014-03-25', '0385753861', '0385753861', '32', 'BOOK', 'Juvenile Fiction', '4', '8', 'en', 'http://books.google.com/books/content?id=Oaw9AQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'A New York Times bestseller When you babysit a grandma, if you\'re lucky . . . it\'s a sleepover at her house! And with the useful tips found in this book, you\'re guaranteed to become an expert grandma-sitter in no time. (Be sure to check out the sections on: How to keep a grandma busy; Things to do at the park; Possible places to sleep, and what to do once you\'re both snugly tucked in for the night.) From the author-illustrator team behind the New York Times bestselling HOW TO... series comes a funny and heartwarming celebration of grandmas and grandchildren.', 10, 25.41, 10),
(17, 'Why Dogs Are Better Than Cats', NULL, 'Bradley Greive, Rachael Hale', 'Bestselling author BRADLEY TREVOR GREIVE is now a household name worldwide since writing THE BLUE DAY BOOK. BTG is up to 20 million books and 115 countries. He is published in 29 languages and has 20 books to his name. His books have won awards internationally and sold more than 20 million copies worldwide. Born in Tasmania, Australia, Bradley spent most of his childhood living in the United Kingdom, Hong Kong and Singapore. A graduate of Australia\'s Royal Military College, he served as a paratroop platoon commander before leaving the army to pursue more creative adventures. He has been bitten by wild monkeys and rabid bats and was accepted into Russia’s cosmonaut training program - though these incidents were probably unrelated. Also an award-winning artist, cartoonist, poet, toy designer, screenwriter and inventor, Bradley lives mostly in the untamed state of Tasmania.  https://www.penguin.com.au/authors/bradley-trevor-greive\r\n\r\n\r\nPortrait photographer Rachael Hale is widely regarded as one of the world\'s most popular photographers and her images have been published on calendars, posters, greeting cards, and stationery around the world. Rachael is the best-selling author 101 Cataclysms: For the Love of Cats and 101 Salutations: For the Love of Dogs. She lives and works in Auckland, New Zealand, with her three cats, Edmund, Gianni, and Versace, and her Dalmatian Boxer cross, Kizzie.  https://www.amazon.com/Rachael-Hale/e/B001ILKFAK%3Fref=dbs_a_mng_rwt_scns_share', 'Andrews McMeel Publishing', '2012-06-05', '0740794051', '0740794051', '224', 'BOOK', 'Pets', '2.5', '3', 'en', 'http://books.google.com/books/content?id=SHsPmtzOC1kC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Some pairings are just meant to be: peanut butter and chocolate, yin and yang, Fred Astaire and Ginger Rogers. So it was only a matter of time before the stars in the universe lined up and suggested the collaboration between New York Times best selling author Bradley Trevor Greive and award-winning photographer Rachael Hale. Teaming up on their first collaborative effort, Greive and Hale explain once and for all Why Dogs Are Better Than Cats. Now, before all you cat lovers find your fur standing on end, Greive is quick to stress that he is simply \"prodog, not anticat. The purpose of this book is not to criticize cats or their owners, but to champion the many exceptional virtues unique to dogs.\" What are these unique attributes that make canine companions superior to their feline fiends? (Oops, we meant friends.) Consider the following: Put simply: * Dogs are social. Cats are sociopaths. * Dogs match up to people. People must match up to cats. * Dogs teach us patience. Cats test our patience. * Dogs give and give. Cats are the gift that keeps on grifting. The bottom line is this: Dogs want love. Cats want fish. Although Greive admits that there is something to be said for \"soft, warm, and sleepy\" (a.k.a. cats) as captured in Hale\'s cuddly feline photographs, he concludes that dogs would be the only ones with character enough to admit this fact, thereby once again positioning themselves as the superior pet, confidant, admirer, and friend.', 10, 19.05, 78),
(16, 'The principles of computer hardware', NULL, 'Alan Clements', 'Boston born Alan Clements, after dropping out of the University of Virginia in his second year, went to the East and become one of the first Westerners to ordain as a Buddhist monk in Myanmar (formerly known as Burma), where he lived at the Mahasi Sasana Yeiktha (MSY) Mindfulness Meditation Centre Yangon (formerly Rangoon) for nearly five years, training in both the practice and teaching of Satipatthana Vipassana (insight) meditation and Buddhist psychology (Abhidhamma), under the guidance of his preceptor the Venerable Mahasi Sayadaw, and his successor Sayadaw U Pandita. In 1984, forced to leave the monastery by Burma’s military authorities, with no reason given, Clements returned to the West and through invitation, lectured widely on the “wisdom of mindfulness,” in addition to leading numerous mindfulness-based meditation retreats and trainings throughout the US, Australia, and Canada, including assisting a three month mindfulness teacher training with Sayadaw U Pandita, at the Insight Meditation Society (IMS), in Massachusetts. In 1988, he integrated into his classical Buddhist training a political awareness that included global human rights, environmental sanity, democracy and the preciousness of everyday freedom. His efforts working on behalf of oppressed peoples led a former director of Amnesty International to call Alan “one of the most important and compelling voices of our times.” As an investigative journalist Alan has lived in some of the most highly volatile areas of the world. In the jungles of Burma, in 1990, he was one of the first eye-witnesses to document the mass oppression of ethnic minorities by Burma’s military, which resulted in his first book, “Burma: The Next Killing Fields?” (with a foreword by the Dalai Lama). Shortly thereafter, Alan was invited to the former-Yugoslavia by a senior officer for the United Nations, where, based in Zagreb during the final year of the war, he wrote the film “Burning” while consulting with NGO’s and the United Nation’s on the “vital role of consciousness in understanding human rights, freedom, and peace.” In 1995, a French publisher asked Alan to attempt reentering Burma for the purpose of meeting Aung San Suu Kyi, the leader of her country’s pro-democracy movement and the recipient of the Nobel Peace laureate in 1991. Just released after six years of incarceration, Alan invited Aung San Suu Kyi to tell her courageous story to the world, thus illuminating the philosophical and spiritual underpinnings of Burma’s nonviolent struggle for freedom, known as a “revolution of the  spirit.” The transcripts of their six months of conversations were smuggled out of the country and became the book “The Voice of Hope.” Translated into numerous languages, The Voice of Hope offers insight into the nature totalitarianism, freedom and nonviolent revolution. Said the London Observer: “Clements is the perfect interlocutor….whatever the future of Burma, a possible future for politics itself is illuminated by these conversations.” Clements is also the co-author with Leslie Kean and a contributing photographer to “Burma’s Revolution of the Spirit” Aperture, NY) – a large format photographic tribute to Burma’s nonviolent struggle for democracy, with a foreword by the Dalai Lama and essays by eight Nobel Peace laureates. In addition, Clements was the script revisionist and principle adviser for Beyond Rangoon (Castle Rock Entertainment), a feature film depicting Burma’s struggle for freedom, directed by John Boorman. In 1999, Alan founded World Dharma, a nonsectarian, multicultural organization of self-styled seekers, artists, writers, scholars, journalists, and activists dedicated to a trans-religious, independent approach to personal and planetary transformation through the integration of global human rights, meditation and the experiential study of consciousness, with one’s life expression through the arts, media, activism, and service. In 2002 Alan wrote “Instinct for Freedom – Finding Liberation Through Living” (World Dharma Publications), a spiritual memoir about his years in Burma and chronicles his pursuit of truth and freedom, while illuminating the framework of the World Dharma vision that also forms the Courses offered through the World Dharma Online Institute (WDOI) that he co-founded with his colleague, Jeannine Davies Ph.D. Instinct for Freedom was nominated for the best spiritual teaching/ memoir by the National Spiritual Booksellers Association in 2003 and has been translated into a numerous languages. Alan’s two most recent books, “Wisdom the for the World – The Requites or Reconciliation: Alan Clements in Conversation with the late Mindfulness Meditation Master, the Venerable Sayadaw U Pandita of Burma,” and “A Future to Believe In – 108 Reflections on the Art and Activism of Freedom,” inspired by and dedicated to his daughter Sahra, has received distinguished praise from numerous leaders and activists, including Dr. Helen Caldicott, Joanna Macy, Dr. Vandana Shiva, Bill McKibben, Paul Hawkin, and Derrick Jensen (the environmental poet laureate) who wrote:  “This culture is killing the planet. If we are to have any future at all, we must unlearn everything the culture has taught us and begin to listen to the planet, to listen to life – the core intelligence of nature and the human heart. This book not only helps us with the unlearning process – the greatest challenge humankind has ever faced – it provides the essential wisdom, the spiritual intelligence, to open ourselves to finally start to hear.” Derrick Jensen  In addition, Alan has presented to such organizations as Mikhail Gorbachev’s State of The World Forum, The Soros Foundation, United Nations Association of San Francisco, the universities of California, Toronto, Sydney, and many others, including a keynote address at the John Ford Theater for Amnesty International’s 30th year anniversary. More recently, Alan was a presented at the Touche Global Consciousness Conference 2019 Bali, Indonesia. He also organizes and co-leads with a Senior Teaching Monk, along with Dr Ingrid Jordt and Dr Jeannine Davies, an annual International Wisdom of Mindfulness Retreat for English speaking participants at the Mahasi Meditation Center Yangon, where he trained as a Buddhist monk in Burma (Myanmar).   http://www.worlddharma.com/about-alan-clements/', 'Oxford University Press, USA', '2000', '31822031048457', '31822031048457', '716', 'BOOK', 'Computers', '3', '45', 'en', 'http://books.google.com/books/content?id=Jd9GAQAAIAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'Ideal for courses in computer hardware, architecture, or digital design, The Principles of Computer Hardware, 3/e, provides a comprehensive introduction to the subject. In Alan Clement\'s noted student-friendly style, it covers core topics including Boolean algebra and logic design; number bases and binary arithmetic; the CPU; assembly language; memory systems; and input/output methods and devices. It then continues with a discussion of the related topics of computer peripherals, the hardware aspects of the operating system, and data communications. This accessible text covers both the 68000 and ARM architectures in a tutorial/lab-based style suitable for a variety of courses. All chapters in this third edition have been rigorously updated. Powerful new Windows-based simulation software allows students to visualize the design of digital circuits. The chapter on RISC has been completely rewritten to include the ARM computer, and the tutorial-based introduction to assembly language now includes many examples.', 10, 25.36, 74),
(15, 'Code', 'The Hidden Language of Computer Hardware and Software', 'Charles Petzold', 'I am the author of a dozen books, most notably two unique explorations of digital computing: Code: The Hidden Language of Computer Hardware and Software http://www.charlespetzold.com/books#code  The Annotated Turing: A Guided Tour through Alan Turing\'s Historic Paper on Computability and the Turing Machine http://www.charlespetzold.com/books#annotated-turing  I grew up in New Jersey and attended Stevens Institute of Technology. After graduating in 1975 with a BS and MS in mathematics, I moved to New York City and worked 10 years for a major insurance company. I was not an actuary, but I did actuarial work and PL/I programming for individual health insurance product development. In 1984 I began writing for PC Magazine, which led to a full-time freelance career that included writing for Microsoft Systems Journal and MSDN magazines. My book Programming Windows was published in six editions between 1988 and 2012, and I also wrote books on OS/2, Windows Forms, the Windows Presentation Foundation, and the C# programming language. In 2014 I began working as a full-time employee for Xamarin (which was acquired in 2016 by Microsoft), where I wrote a book and documentation about Xamarin.Forms. In September 2018 I retired from my 34-year career of writing, speaking, and thinking about application programming interfaces. Links to my books and articles from that career are available on my old website. http://www.charlespetzold.com/home.html I am currently working on what I hope to be a series of books on the historical, social, political, and philosophical foundations of computing. Tentative titles are: Computer of the Tides: Lord Kelvin\'s Machine to Disprove Evolution The Mathematics of Miracles: Charles Babbage and the Ninth Bridgewater Treatise Silk and Software: The Story of the Jacquard Loom The Rise and Fall of Logarithms Wish me luck.  http://www.charlespetzold.com/about/', 'Microsoft Press', '2000-10-11', '0735638721', '0735638721', '400', 'BOOK', 'Computers', '4', '2', 'en', 'http://books.google.com/books/content?id=iptCAwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'What do flashlights, the British invasion, black cats, and seesaws have to do with computers? In CODE, they show us the ingenious ways we manipulate language and invent new means of communicating with each other. And through CODE, we see how this ingenuity and our very human compulsion to communicate have driven the technological innovations of the past two centuries. Using everyday objects and familiar language systems such as Braille and Morse code, author Charles Petzold weaves an illuminating narrative for anyone who’s ever wondered about the secret inner life of computers and other smart machines. It’s a cleverly illustrated and eminently comprehensible story—and along the way, you’ll discover you’ve gained a real context for understanding today’s world of PCs, digital media, and the Internet. No matter what your level of technical savvy, CODE will charm you—and perhaps even awaken the technophile within.', 10, 18.63, 1),
(13, 'Introduction to Information Retrieval', NULL, 'Christopher D. Manning, Prabhakar Raghavan', 'Thomas M. Siebel Professor in Machine Learning, Professor of Linguistics and of Computer Science Director, Stanford Artificial Intelligence Laboratory (SAIL) Natural Language Processing Group, Stanford AI Lab, Linguistics and Computer Science, Stanford University Bio Christopher Manning is the inaugural Thomas M. Siebel Professor in Machine Learning in the Departments of Computer Science and Linguistics at Stanford University and Director of the Stanford Artificial Intelligence Laboratory (SAIL). His research goal is computers that can intelligently process, understand, and generate human language material. Manning is a leader in applying Deep Learning to Natural Language Processing, with well-known research on Tree Recursive Neural Networks, the GloVe model of word vectors, sentiment analysis, neural network dependency parsing, neural machine translation, question answering, and deep language understanding. He also focuses on computational linguistic approaches to parsing, robust textual inference and multilingual language processing, including being a principal developer of Stanford Dependencies and Universal Dependencies. Manning has coauthored leading textbooks on statistical approaches to Natural Language Processing (NLP) (Manning and Schütze 1999) and information retrieval (Manning, Raghavan, and Schütze, 2008), as well as linguistic monographs on ergativity and complex predicates. He is an ACM Fellow, a AAAI Fellow, and an ACL Fellow, and a Past President of the ACL (2015). His research has won ACL, Coling, EMNLP, and CHI Best Paper Awards. He has a B.A. (Hons) from The Australian National University and a Ph.D. from Stanford in 1994, and he held faculty positions at Carnegie Mellon University and the University of Sydney before returning to Stanford. He is the founder of the Stanford NLP group (@stanfordnlp) and manages development of the Stanford CoreNLP software. Contact M Dept of Computer Science, Gates Building 2A, 353 Serra Mall, Stanford CA 94305-9020, USA E manning@cs.stanford.edu T @chrmanning W +1 (650) 723-7683 F +1 (650) 725-1449 R Gates 248 O Fri 2-3pm A Suzanne Lessard, Gates 148, +1 (650) 723-6319, slessard@stanford.edu Brief CV I\'m Australian (“I come from a land of wide open spaces …”) BA (Hons) Australian National University 1989 (majors in mathematics, computer science and linguistics) PhD Stanford Linguistics 1994 Asst Professor Carnegie Mellon University Computational Linguistics Program 1994-96 Lecturer University of Sydney Dept of Linguistics 1996-99 Asst Professor Stanford University Depts of Computer Science and Linguistics 1999-2006 Assoc Professor Stanford University Depts of Linguistics and Computer Science 2006-2012 Professor Stanford University Depts of Linguistics and Computer Science 2012- President of the Association for Computational Linguistics 2015 Papers Here is my publications list. However, I\'ve become lazy, so for recent stuff, you\'re often more likely to find it on the NLP Group publications page. Or things might be more up-to-date at Google Scholar, Semantic Scholar, or Microsoft Academic. Books Introduction to Information Retrieval, with Hinrich Schütze and Prabhakar Raghavan (Cambridge University Press, 2008). Manning and Schütze, Foundations of Statistical Natural Language Processing (MIT Press, 1999). Complex Predicates and Information Spreading in LFG (1999). Ergativity: Argument Structure and Grammatical Relations (1996). Conferences and Talks Some of my talks are available online. In 2013, I was program co-chair for the first International Conference on Learning Representations (see: ICLR 2013). The 2013 edition was a really fun workshop-scale event. Since then, ICLR has grown in size exponentially. In 2013, I helped organize the first CVSC workshop. It was a really lively workshop. I also helped organize a second Workshop on Continuous Vector Space Models and their Compositionality at EACL 2014. I helped organize a Workshop on Interactive Language Learning, Visualization, and Interfaces to be held at ACL 2014, trying to build an interdisciplinary community interested in the intersection of NLP, HCI, and data visualization. Students I made a page listing all my Ph.D. graduates. You can find all my current students on the Stanford NLP Group People page. Research Projects The general area of my research is robust but linguistically sophisticated natural language understanding, and opportunities to use it in real-world domains. Particular current topics include deep learning for NLP, Universal Dependencies and dependency parsing, language learning through interaction, and reading comprehension. My research at Stanford is currently supported by the NSF, DARPA, Bloomberg, Tencent, UST Global. I am interested in new students, at or accepted to Stanford, wanting to work in the area of Natural Language Processing. To find out more about what I do, it\'s best to look at my papers, or my group research page. Unadmitted students: The above statement is directed at students who have already been admitted to Stanford. I don\'t do admissions. You need to apply to a program in the usual manner; for Linguistics see http://www-linguistics.stanford.edu/graduate/admissions.shtml, and for Computer Science, see http://cs.stanford.edu/Admissions/. PhD students in CS/Linguistics or allied fields: please contact me directly. Masters students: I often employ a couple of masters students. Most appealing are people with a background in NLP, and time to devote to an RAship. It helps your case to have done well in CS 224N: NLP. Undergraduate students in CS/Linguistics or allied fields: please contact me directly. Courses Online videos! You can find complete videos for three courses on NLP that I have (co-)taught online: Natural Language Processing (a.k.a. the 2012 Coursera NLP-class) by Dan Jurafsky and Christopher Manning on YouTube [slides]. If you don\'t have much background in AI, ML, or NLP, you should start with this class. Natural Language Processing (a.k.a. CS224N Spring 2008) by Christopher Manning [slides]. This is an aging version of my traditional probabilistic NLP course. It looks like you can only watch these videos with Flash. :( Lecture Collection | Natural Language Processing with Deep Learning (a.k.a. CS224N Winter 2017) by Christopher Manning and Richard Socher on YouTube[slides]. If you\'re ready to dive into the latest in deep learning for NLP, you should do this course. It assumes more mathematics prerequisites (multivariate calc, linear algebra). In Fall 2016, I taught Linguistics 278: Programming for linguists (and any other digital humanities or text-oriented social science students who think it might be a good match). . I co-taught tutorials on Deep Learning for NLP at ACL 2012 with Yoshua Bengio and Richard Socher, and at NAACL 2013 with Richard Socher. Slides, references, and videos are available. In 2012, I co-taught a free online course on Natural Language Processing, one of the earliest MOOCs on Coursera, with Dan Jurafsky. We haven\'t found the time to revise it and teach a second version, but you can watch all the videos (see above). In June 2011, I taught a tutorial Natural Language Processing Tools for the Digital Humanities at Digital Humanities 2011 at Stanford. Nearly every year, I teach CS 276: Information Retrieval and Web Search, with Pandu Nayak. Earlier versions of this course include two years of two-quarter sequences CS276A/B on information retrieval and text information classification and extraction, broadly construed (\"IR++\"): Fall quarter course website. Winter quarter course website. This course started in 2001. Early versions were also co-taught by me, Prabhakar Raghavan, and Hinrich Schütze. Nearly every year, I teach CS 224N / Ling 237. Natural Language Processing -- Develops an in-depth understanding of both the algorithms available for the processing of linguistic information and the underlying computational properties of natural languages. Morphological, syntactic, and semantic processing from both a linguistic and an algorithmic perspective. Focus on modern quantitative techniques in NLP: using large corpora, statistical models for acquisition, disambiguation, and parsing. Examination and construction of representative systems. Prerequisites: 121/221 or Ling 138/238, and programming experience. Recommended: basic familiarity with logic and probability. 3 units. I\'ve taught this course yearly since Spr 2000. Many previous student projects are available online. In fall 2007 I taught Ling 289: Quantitative and Probabilistic Explanation in Linguistics MW 2:15-3:45 in 160-318. I previously taught it in winter 2002 (née Ling 236) and Winter 2005 (as Ling 235). In the summer of 2007, I taught at the LSA Linguistic Institute: Statistical Parsing and Computational Linguistics in Industry. In fall 1999 and winter 2001, I taught CS 121 Artificial Intelligence. The text book was S. Russell and P. Norvig, Artificial Intelligence: A Modern Approach. I ran the NLP Reading Group from 1999-2002. The NLP Reading Group is now student organized. Other stuff LaTeX: When I used to have more time (i.e., when I was a grad student), I used to spend some of it writing (La)TeX macros. [Actually, that\'s a lie; I still spend some time doing it....] We\'ve got two sons: Joel [linkedin, github] and Casey [linkedin, github]. Here are my opinions on books for kids.  https://nlp.stanford.edu/manning/\r\n\r\n\r\n\r\nPrabhakar Raghavan is a Senior Vice President at Google, and oversees Google’s Advertising and Commerce products, including search, display and video advertising, analytics, shopping, payments, and travel. In addition, he is responsible for infrastructure teams that provide much of the software backbone for search, ads and other groups.\r\nPreviously, he served as Vice President of Google Apps, Google Cloud, overseeing engineering, products and user experience. Under his leadership, the Apps business expanded from a set of consumer apps to an enterprise solution that is a major contributor to Google’s Cloud business. He also grew both Gmail and Drive past 1 billion MAUs and introduced a number of machine intelligence features in G Suite, including Smart Reply, Smart Compose, Drive Quick Access — each leading to measurable improvements in user experience.\r\nPrior to joining Google, Prabhakar founded and led Yahoo! Labs where he was responsible for search and ad ranking, as well as ad marketplace design, and later served as the company’s Chief Strategy Officer. He also served as CTO at Verity, and held various positions over the course of 14 years at IBM with a focus on algorithms, data mining and machine learning.\r\nPrabhakar holds a Ph.D. from U.C. Berkeley in Electrical Engineering and Computer Science and a Bachelor of Technology from the Indian Institute of Technology, Madras. His research background spans algorithms, web search and databases. He is the co-author of two widely-used graduate texts on algorithms and on search: Randomized Algorithms and Introduction to Information Retrieval; a member of the National Academy of Engineering, a Fellow of the ACM and IEEE; and was a Consulting Professor of Computer Science at Stanford University. In 2009, he was awarded a Laurea honoris causa from the University of Bologna. He has served as editor in chief for the Journal of the ACM, has published over 100 papers in various fields and holds 20 issued patents, including several on link analysis for web search.  https://ai.google/research/people/PrabhakarRaghavan', 'Cambridge University Press', '2008-07-07', '1139472100', '1139472100', NULL, 'BOOK', 'Computers', '4.5', '5', 'en', 'http://books.google.com/books/content?id=t1PoSh4uwVcC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Class-tested and coherent, this textbook teaches classical and web information retrieval, including web search and the related areas of text classification and text clustering from basic concepts. It gives an up-to-date treatment of all aspects of the design and implementation of systems for gathering, indexing, and searching documents; methods for evaluating systems; and an introduction to the use of machine learning methods on text collections. All the important ideas are explained using examples and figures, making it perfect for introductory courses in information retrieval for advanced undergraduates and graduate students in computer science. Based on feedback from extensive classroom experience, the book has been carefully structured in order to make teaching more natural and effective. Slides and additional exercises (with solutions for lecturers) are also available through the book\'s supporting website to help course instructors prepare their lectures.', 10, 16.25, 4),
(14, 'Nine Algorithms That Changed the Future', 'The Ingenious Ideas That Drive Today\'s Computers', 'John MacCormick', 'John MacCormick is a computer science teacher and researcher. He grew up in New Zealand, studied mathematics and computer science in England, and now lives in Pennsylvania, USA. MacCormick has a PhD in computer vision from the University of Oxford, has worked in the research labs of Hewlett-Packard and Microsoft, and is currently a professor of computer science at Dickinson College. His work spans several sub-fields of computer science, including computer vision, large-scale distributed systems, computer science education, and the public understanding of computer science.   https://www.amazon.com/John-MacCormick/e/B001K8C47S/ref=dp_byline_cont_pop_book_1', 'Princeton University Press', '2013-05-05', '0691158193', '0691158193', '232', 'BOOK', 'Computers', '4.5', '5', 'en', 'http://books.google.com/books/content?id=JmmYDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Every day, we use our computers to perform remarkable feats. A simple web search picks out a handful of relevant needles from the world\'s biggest haystack: the billions of pages on the World Wide Web. Uploading a photo to Facebook transmits millions of pieces of information over numerous error-prone network links, yet somehow a perfect copy of the photo arrives intact. Without even knowing it, we use public-key cryptography to transmit secret information like credit card numbers; and we use digital signatures to verify the identity of the websites we visit. How do our computers perform these tasks with such ease? This is the first book to answer that question in language anyone can understand, revealing the extraordinary ideas that power our PCs, laptops, and smartphones. Using vivid examples, John MacCormick explains the fundamental \"tricks\" behind nine types of computer algorithms, including artificial intelligence (where we learn about the \"nearest neighbor trick\" and \"twenty questions trick\"), Google\'s famous PageRank algorithm (which uses the \"random surfer trick\"), data compression, error correction, and much more. These revolutionary algorithms have changed our world: this book unlocks their secrets, and lays bare the incredible ideas that our computers use every day.', 10, 37.19, 52),
(21, 'Up in the Garden and Down in the Dirt', NULL, 'Kate Messner', 'Kate Messner is passionately curious and writes books that encourage kids to wonder, too. Kate’s titles include both fiction and nonfiction; she writes award-winning picture books, easy readers, chapter book series, and novels for young readers. Her books are frequently nominated for state book award lists and have been honored with the E.B. White Read Aloud Award, Golden Kite and Crystal Kite Awards, Riverby Award for Natural History Writing, and Nerdy Book Club Awards. Kate’s science themed books have also been selected as finalists for the AAAS/Subaru SB&F Prize and named to the NSTA/CBC list of Outstanding Science Trade Books for Students K-12.  Kate is a frequent speaker at conferences for writers and educators and visits schools all over the world to talk with students about books, writing, and the power of curiosity.   https://www.katemessner.com', 'Chronicle Books', '2015-03-03', '1452144192', '1452144192', '52', 'BOOK', 'Juvenile Fiction', '4.5', '2', 'en', 'http://books.google.com/books/content?id=jgBiBQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'In this exuberant and lyrical follow-up to the award-winning Over and Under the Snow, discover the wonders that lie hidden between stalks, under the shade of leaves . . . and down in the dirt. Explore the hidden world and many lives of a garden through the course of a year! Up in the garden, the world is full of green—leaves and sprouts, growing vegetables, ripening fruit. But down in the dirt exists a busy world—earthworms dig, snakes hunt, skunks burrow—populated by all the animals that make a garden their home. Plus, this is the fixed format version, which will look almost identical to the print version. Additionally for devices that support audio, this ebook includes a read-along setting.', 10, 19.95, 98),
(22, 'In a Heartbeat', NULL, 'Loretta Ellsworth', 'I was born in Mason City, Iowa, and grew up in a large family of seven children, two parents, and one bathroom. When I was young I kept a diary (see below) but I never considered becoming a writer until I had four children of my own. My first book took me two years to write. I juggled writing with my work as a middle-school Spanish teacher, my kids\' soccer games, and lots of family get-togethers. Other facts about me: When I was little, I loved cats. I can’t swim (all those years of lessons wasted). I’ve been in a writer’s group for many years. I’ve never eaten sushi. Favorite book? To Kill a Mockingbird, by Harper Lee Favorite old movie? \"Harvey,\" with Jimmy Stewart. Favorite line from that movie: “In this world, you must be oh so smart or oh so pleasant. Well, for years I was smart. I recommend pleasant.” My childhood diary was filled with boring details. An example: \"Had Girls Scouts today and watched Bonanza.\" (I watched a lot of Bonanza). But writing in my diary did get me into the habit of writing every day. (A discipline that comes in handy to most writers).  https://www.lorettaellsworth.com/about/about1.html', 'Bloomsbury Publishing USA', '2010-08-10', '0802722458', '0802722458', '224', 'BOOK', 'Juvenile Fiction', '4.5', '36', 'en', 'http://books.google.com/books/content?id=fkSf0WMljCgC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'When a small mistake costs sixteen-year-old Eagan her life during a figure-skating competition, she leaves many things unreconciled, including her troubled relationship with her mother. From her vantage point in the afterlife, Eagan reflects back on her memories, and what she could have done differently, through her still-beating heart. When fourteen-year-old Amelia learns she will be getting a heart transplant, her fear and guilt battle with her joy at this new chance at life. And afterwards when she starts to feel different-dreaming about figure skating, craving grape candy-her need to learn about her donor leads her to discover and explore Eagan\'s life, meeting her grieving loved ones and trying to bring the closure they all need to move on. Told in alternating viewpoints, In a Heartbeat tells the emotional and compelling story of two girls sharing one heart.', 10, 19.95, NULL),
(23, 'App Inventor 2', 'Create Your Own Android Apps', 'David Wolber, Hal Abelson', 'Professor Wolber runs USF’s Democratize Computing Lab and focuses on empowering artists, designers, kids, women, men, humanity majors, business students — makers of all types — to add coding to their creative arsenals. He is a leader in teaching beginners to learn coding by programming phones and tablets using the visual language App Inventor. His appinventor.org site has helped over 1.5 million new app creators, and his course-in-a-box materials have served as a template for numerous App Inventor courses at the K-12 and university levels. Wolber developed many of the tutorials for Google’s original App Inventor site, and he is the lead author of App Inventor: Create your own Android Apps (version 2 out October 2014!), along with App Inventor creators Hal Abelson, Ellen Spertus and Liz Looney. He is also a co-author of an on-line course and professional development materials for the new Computational Thinking Advanced Placement (AP) course for US High Schools (mobile-csp.org). Wolber recently completed a sabbatical at MIT where he contributed to the release of App Inventor 2. USF students and he are leading the development of the App Inventor Community Gallery that will be integrated into the system in coming months. Wolber and the Democratize Computing Lab are funded by the W.M. Keck Foundation as well as a multi-university NSF grant in collaboraton with MIT, Wellesley, Trinity, and UMass-Lowell.  https://www.usfca.edu/faculty/david-wolber', '\"O\'Reilly Media, Inc.\"', '2014-10-13', '1491907231', '1491907231', '360', 'BOOK', 'Computers', '1.5', '64', 'en', 'http://books.google.com/books/content?id=GzgCBQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'Yes, you can create your own apps for Android devices—and it’s easy to do. This extraordinary book introduces you to App Inventor 2, a powerful visual tool that lets anyone build apps. Learn App Inventor basics hands-on with step-by-step instructions for building more than a dozen fun projects, including a text answering machine app, a quiz app, and an app for finding your parked car! The second half of the book features an Inventor’s Manual to help you understand the fundamentals of app building and computer science. App Inventor 2 makes an excellent textbook for beginners and experienced developers alike. Use programming blocks to build apps—like working on a puzzle Create custom multi-media quizzes and study guides Design games and other apps with 2D graphics and animation Make a custom tour of your city, school, or workplace Control a LEGO® MINDSTORMS® NXT robot with your phone Build location-aware apps by working with your phone’s sensors Explore apps that incorporate information from the Web', 10, 19.01, 2),
(1518, 'But how Do it Know?', 'The Basic Principles of Computers for Everyone', 'J. Clark Scott', '\"J. Clark Scott has had a long and diverse career in the computer industry, starting at large companies such as IBM and Intel, and eventually becoming the author of four successful consumer software packages. Early in his career, he noticed how confused some of his friends were about computers and gave them lectures to teach them how simple the basics really were. It was at that time that the idea for this book was born. This is his first book, but one that has been in the works for decades.  https://www.barnesandnoble.com/w/but-how-do-it-know-j-clark-scott/1116793331\"', 'John C Scott', '2009-07-04', '0615303765', '0615303765', '222', 'BOOK', 'Computers', '4.5', '10', 'en', 'http://books.google.com/books/content?id=-XGAPeVhRs4C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'This book thoroughly explains how computers work. It starts by fully examining a NAND gate, then goes on to build every piece and part of a small, fully operational computer. The necessity and use of codes is presented in parallel with the apprioriate pieces of hardware. The book can be easily understood by anyone whether they have a technical background or not. It could be used as a textbook.', 10, 19.99, 36),
(1517, 'Algorithms to Live By', 'The Computer Science of Human Decisions', 'Brian Christian, Tom Griffiths', 'Brian Christian is the author of The Most Human Human, which was named a Wall Street Journal bestseller, a New York Times Editors’ Choice, and a New Yorker favorite book of the year. He is the author, with Tom Griffiths, of Algorithms to Live By, a #1 Audible bestseller, Amazon best science book of the year and MIT Technology Review best book of the year. Christian’s writing has been translated into nineteen languages, and has appeared in The New Yorker, The Atlantic, Wired, The Wall Street Journal, The Guardian, The Paris Review, and in scientific journals such as Cognitive Science. Christian has been featured on The Daily Show with Jon Stewart, Radiolab, and The Charlie Rose Show, and has lectured at Google, Facebook, Microsoft, the Santa Fe Institute, and the London School of Economics. His work has won several awards, including fellowships at Yaddo and the MacDowell Colony, publication in Best American Science & Nature Writing, and an award from the Academy of American Poets. Born in Wilmington, Delaware, Christian holds degrees in philosophy, computer science, and poetry from Brown University and the University of Washington. A Visiting Scholar at the University of California, Berkeley, the Director of Technology at McSweeney’s Publishing, and an active open-source contributor to projects such as Ruby on Rails, he lives in San Francisco. For speaking inquiries, please contact Wesley Neff at the Leigh Bureau: wesn@leighbureau.com. info@brianchristian.org  https://brianchristian.org/bio-contact/TOM GRIFFITH', 'Macmillan', '2016-04-19', '1627790365', '1627790365', '368', 'BOOK', 'Business & Economics', '4.5', '14', 'en', 'http://books.google.com/books/content?id=Ydn_CwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'A fascinating exploration of how insights from computer algorithms can be applied to our everyday lives, helping to solve common decision-making problems and illuminate the workings of the human mind All our lives are constrained by limited space and time, limits that give rise to a particular set of problems. What should we do, or leave undone, in a day or a lifetime? How much messiness should we accept? What balance of new activities and familiar favorites is the most fulfilling? These may seem like uniquely human quandaries, but they are not: computers, too, face the same constraints, so computer scientists have been grappling with their version of such issues for decades. And the solutions they\'ve found have much to teach us. In a dazzlingly interdisciplinary work, acclaimed author Brian Christian and cognitive scientist Tom Griffiths show how the algorithms used by computers can also untangle very human questions. They explain how to have better hunches and when to leave things to chance, how to deal with overwhelming choices and how best to connect with others. From finding a spouse to finding a parking spot, from organizing one\'s inbox to understanding the workings of memory, Algorithms to Live By transforms the wisdom of computer science into strategies for human living.', 10, 19.99, NULL);
